DEBUG = trueinit = truerespawn_time = 30barrel = nilpos = mkVec3.new(0, 2, 0)done = truefunction onPostCreate()	barrel = FindObject("barrel_1")	pos = GetWorldPosition(barrel)endfunction onUpdate()		pos = GetWorldPosition(barrel)	if done then		barrel = CreateObject(this, "ExplodingObject", "barrel_of_destruction")		SetFieldValue(barrel:GetObject(), "Name", "barrel_5")		SetWorldPosition(barrel:GetObject(), mkVec3.new(110.0, 0.5, 54.0))		done = false	end		end