DEBUG = trueDMG_POISON = 3testing_barrel = nilbarrel_of_destruction = nilexplosion_time = 0increased_damage_time = 30check_time = truedone = true;last_damage_times = {}dmg_period = 2.0dps = 10function onTouched(touched_object)	--	if DEBUG then LogError("Touched " .. GetObjectName(touched_object:GetObject())) end				last_dmg_time = last_damage_times[GetObjectName(touched_object:GetObject())]	--if DEBUG then LogError("Damage:"..dps) end	if last_dmg_time == nil or GetTime() - last_dmg_time > dmg_period then		if (explosion_time + increased_damage_time) <  GetTime() then dps = 10 end		dmg_value = dps / dmg_period		GiveDamage(this, touched_object, DMG_POISON, dmg_value, mkVec3.new(), mkVec3.new())		last_damage_times[GetObjectName(touched_object:GetObject())] = GetTime()	endendfunction onPostCreate()	testing_barrel = FindObject("barrel_1")	barrel_of_destruction = FindObject("barrel_2")endfunction onUpdate()		if "lava" == GetObjectName(this) then dps = 5 end		if "mid_lava_1" == GetObjectName(this) then dps = 7 end--increase periodically damage	if not testing_barrel:IsValid() then		if lava == this then			dps = 11			if check_time then 				explosion_time = GetTime()				check_time = false			end		end	end--delete object		if "lava" == GetObjectName(this) then 		if not testing_barrel:IsValid()  then			if not barrel_of_destruction:IsValid() then				if done then 					DestroyObject(this)					--CreateObject(this, "CylinderVolumeTrigger", "test_lava_lake")					done = false;				end			end		end	endend